<!-- image Section -->
<section id="section4" class="heroSection smooth-scroll">
  <div class="image-container flex space-x-4 mx-auto overflow-x-auto">
    <img
      src={getImageUrl(studioContent?.gallery[0])}
      alt="Studio workspace with modern design"
      class="object-cover flex-grow-[2] basis-1/4 aspect-[3/4]"
    />
    <img
      src={getImageUrl(studioContent?.gallery[1])}
      alt="Studio workspace cafe with modern design"
      class="object-cover flex-grow-[3] basis-1/3 aspect-[4/5]"
    />
    <img
      src={getImageUrl(studioContent?.gallery[2])}
      alt="Studio workspace portrait with modern design"
      class="object-cover flex-grow basis-1/5 aspect-[5/6]"
    />
    <img
      src={getImageUrl(studioContent?.gallery[3])}
      alt="Studio architect workspace portrait"
      class="object-cover flex-grow-[1.5] basis-1/6 aspect-[3/5]"
    />
    <img
      src={getImageUrl(studioContent?.gallery[4])}
      alt="black and white field"
      class="object-cover flex-grow-[2.5] basis-1/4 aspect-[16/9]"
    />
  </div>
</section>


  <!-- Section 01 -->
  {#if studioContent.section01}
    <section id="section1" class="section01 smooth-scroll grid md:grid-cols-2 grid-cols-1 gap-6 p-6 mt-10 items-start">
      <h1 class="text-4xl font-serif">{studioContent.section01.title}</h1>
      <div class="contentParent space-y-4">
        <a href="#" class="link-accent underline">Get In Touch</a>
        <p>{studioContent.section01.part1}</p>
        <p>{studioContent.section01.part2}</p>
      </div>
    </section>
  {/if}

  <!-- Projects -->
  <section
   id="section2"
  class="projectSection smooth-scroll"
  role="region" 
  aria-label="Projects Section" 
>
  <!-- Custom Cursor -->
  <div class="custom-cursor" >
    <div class="cursor-text">Visit Project &nbsp; Visit Project &nbsp; Visit Project</div>
  </div>

  <!-- Projects -->
  <a 
  href={`/work/${displayedProjects[0].slug.current}`}
  >
  <div
    class="projectParent">
    <img
      src={displayedProjects[0].heroImage?.asset?.url}
      alt={displayedProjects[0].projectTitle}
      class="project1 object-cover"
    />

    <div class="projectDetails">
      <h3 class="p-sm">{displayedProjects[0].projectTitle}</h3>
      <p class="p-sm">({displayedProjects[0].completionDate})</p>
    </div>
  </div>
  </a>

  <a 
  href={`/work/${displayedProjects[1].slug.current}`}
  >
  <div
    class="projectParent"
  >
    <img
      src={displayedProjects[1].heroImage?.asset?.url}
      alt={displayedProjects[1].projectTitle}
      class="project2 object-cover"
    />
    <div class="projectDetails">
      <h3 class="p-sm">{displayedProjects[1].projectTitle}</h3>
      <p class="p-sm">({displayedProjects[1].completionDate})</p>
    </div>
  </div>
  </a>

  <a 
  href={`/work/${displayedProjects[2].slug.current}`}
  >
  <div
    class="projectParent"
  >
    <img
      src={displayedProjects[2].heroImage?.asset?.url}
      alt={displayedProjects[2].projectTitle}
      class="project3 object-cover"
    />
    <div class="projectDetails">
      <h3 class="p-sm">{displayedProjects[2].projectTitle}</h3>
      <p class="p-sm">{displayedProjects[2].completionDate}</p>
    </div>
  </div>
</a>
</section>

  <!-- Section 02 -->
  {#if studioContent.section02}
    <section id="section3" class="section02 smooth-scroll grid md:grid-cols-2 grid-cols-1 gap-6 p-6 mt-10 items-start">
      <div class="rightContent space-y-4">
        <h2 class="text-3xl font-serif">{studioContent.section02.title}</h2>
        <p>{studioContent.section02.description}</p>
        <a href="#" class="link-accent underline">Get In Touch</a>
        
