      </div>
      <div class="leftContent space-y-6">
        
        <div class="contentParent space-y-2">
          <h3 class="subheadings">{studioContent.section02.subHeading1}</h3>
          <p>{studioContent.section02.part1}</p>
        </div>
        <div class="contentParent space-y-2">
          <h3 class="subheadings">{studioContent.section02.subHeading2}</h3>
          <p>{studioContent.section02.part2}</p>
        </div>
      </div>
    </section>
  {/if}

  <hr class="line my-12 border-t border-gray-300" />

<!-- Section 06 -->
<section id="section6" class="section06 smooth-scroll">
  <div class="header"> 
    <div class="title">
      <h1 class="text-4xl">PARTNERS</h1>
    <p class="p-accent">{studioContent.faces.theTeam}</p>
    </div>

    <div class="smallBox">  
    <img
    src={getImageUrl(studioContent.studioImage.asset)}
    class="studio-image" />
  </div>

</div>

  <div class="partner-list">
    {#each Array.isArray(studioContent.faces.partnerList) ? studioContent.faces.partnerList : [] as partner}
      
    <div class="partner">
        <!-- <img src={getImageUrl(partner.image.asset)} alt={partner.name} /> -->
        <span class="partnerName">
          <a href={partner.linkedIn} target="_blank">
          <h1 class="size-4xl">{partner.name}</h1>
          </a>
        </span>

        <span class="partnerRole">
          <p class="subheadings">/{partner.role}</p>
        </span>
        {#if partner.linkedIn}
        {/if}
      </div>
    {/each}
  </div>
</section>


</section>
